tinymce.PluginManager.add("image4be", function (a, b) {
    a.addButton("image4be", {
        title: "Insert image",
        icon: "image",
        onclick: function () {
            a.windowManager.open({
                title: "Insert image",
                width: window.innerWidth * 0.9,
                height: window.innerHeight * 0.8,
                body: [{
                    type: "textbox",
                    name: "title",
                    label: "Title"
                }],
                onsubmit: function (b) {
                    a.insertContent("Title: " + b.data.title)
                }
            })
        }
    }), a.addMenuItem("example", {})
});